import { CommonModule } from '@angular/common';
import { Component, OnInit, OnDestroy } from '@angular/core';

@Component({
  selector: 'app-experience',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './experience.component.html',
  styleUrls: ['./experience.component.css']
})
export class ExperienceComponent {
  experience = [
    {
      position: '.NET Developer',
      company: 'LOXAM',
      period: 'May 2024 - Present',
      points: [
        'Enhanced API performance by integrating Service Bus Trigger Functions',
        'Implemented Azure B2C role-based authentication',
        'Optimized API performance through caching implementation'
      ]
    },
    {
      position: '.NET Developer',
      company: 'LOXAM',
      period: 'May 2024 - Present',
      points: [
        'Enhanced API performance by integrating Service Bus Trigger Functions',
        'Implemented Azure B2C role-based authentication',
        'Optimized API performance through caching implementation'
      ]
    },
    {
      position: '.NET Developer',
      company: 'LOXAM',
      period: 'May 2024 - Present',
      points: [
        'Enhanced API performance by integrating Service Bus Trigger Functions',
        'Implemented Azure B2C role-based authentication',
        'Optimized API performance through caching implementation'
      ]
    },
    {
      position: '.NET Developer',
      company: 'Upwork',
      period: 'August 2019 - May 2024',
      points: [
        'Designed and implemented microservices using Azure Service Fabric',
        'Developed payment solutions using Stripe and PayPal REST API',
        'Implemented layer architecture and RESTful APIs'
      ]
    }
  ];
}
<section class="container mx-auto px-4 py-16">
  <h2 class="text-3xl font-bold mb-8 text-center">Professional Experience</h2>
  <div class="max-w-4xl mx-auto space-y-8">
    <div *ngFor="let job of experience; let i = index" [ngClass]="{'bg-gray-300': i === 0, 'bg-gray': i % 2 !== 0}"
      class="p-6 rounded-lg shadow">
      <div class="flex flex-col md:flex-row justify-between mb-4">
        <div>
          <h3 class="text-xl font-bold text-blue-600">{{job.position}}</h3>
          <p class="text-lg">{{job.company}}</p>
        </div>
        <p class="text-gray-600">{{job.period}}</p>
      </div>
      <ul class="list-disc pl-5 space-y-2">
        <li *ngFor="let point of job.points" class="text-gray-700">
          {{point}}
        </li>
      </ul>
    </div>
  </div>
</section>
