import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { 
  faCode, 
  faDesktop, 
  faDatabase, 
  faCloud 
} from '@fortawesome/free-solid-svg-icons';
import {
  faJava,
  faPython,
  faNodeJs,
  faAngular,
  faReact,
  faHtml5,
  faCss3,
  faAws,
  faDocker
} from '@fortawesome/free-brands-svg-icons';

interface SkillCategory {
  name: string;
  skills: Array<{
    name: string;
    icon: any;
  }>;
  icon: any;
}

@Component({
  selector: 'app-skills',
  standalone: true,
  imports: [CommonModule, FontAwesomeModule],
  templateUrl: './skills.component.html',
  styleUrls: ['./skills.component.css']
})
export class SkillsComponent {
  // Category icons
  faCode = faCode;
  faDesktop = faDesktop;
  faDatabase = faDatabase;
  faCloud = faCloud;

  skillCategories: SkillCategory[] = [
    {
      name: 'Backend',
      icon: faCode,
      skills: [
        { name: 'C#', icon: faCode },
        { name: '.NET Core', icon: faCode },
        { name: 'Python', icon: faPython },
        { name: 'Node.js', icon: faNodeJs },
        { name: 'Java', icon: faJava }
      ]
    },
    {
      name: 'Frontend',
      icon: faDesktop,
      skills: [
        { name: 'Angular', icon: faAngular },
        { name: 'React', icon: faReact },
        { name: 'TypeScript', icon: faCode },
        { name: 'HTML', icon: faHtml5 },
        { name: 'CSS', icon: faCss3 }
      ]
    },
    {
      name: 'Database',
      icon: faDatabase,
      skills: [
        { name: 'SQL Server', icon: faDatabase },
        { name: 'MongoDB', icon: faDatabase },
        { name: 'PostgreSQL', icon: faDatabase },
        { name: 'Redis', icon: faDatabase }
      ]
    },
    {
      name: 'Cloud & DevOps',
      icon: faCloud,
      skills: [
        { name: 'Azure', icon: faCloud },
        { name: 'AWS', icon: faAws },
        { name: 'Docker', icon: faDocker },
        { name: 'CI/CD', icon: faCode }
      ]
    }
  ];
}
<section class="container mx-auto px-4 py-16">
  <h2 class="text-3xl font-bold mb-8 text-center">Technical Skills</h2>
  <div class="grid md:grid-cols-2 lg:grid-cols-4 gap-6 max-w-7xl mx-auto">
    <div *ngFor="let category of skillCategories" 
         class="bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition-all duration-300">
      <div class="flex items-center gap-2 mb-4">
        <fa-icon [icon]="category.icon" class="text-blue-600 text-xl"></fa-icon>
        <h3 class="text-xl font-bold text-blue-600">{{category.name}}</h3>
      </div>
      <div class="flex flex-wrap gap-2">
        <span *ngFor="let skill of category.skills" 
              class="flex items-center gap-1 px-3 py-1 bg-gray-100 rounded-full text-gray-700 text-sm hover:bg-blue-100 hover:text-blue-700 transition-colors duration-300 cursor-default">
          <fa-icon [icon]="skill.icon" class="text-sm"></fa-icon>
          {{skill.name}}
        </span>
      </div>
    </div>
  </div>
</section>
