import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';

interface SkillCategory {
  name: string;
  skills: string[];
  icon?: string; // Optional icon for each category
}

@Component({
  selector: 'app-skills',
  standalone: true,
  imports: [CommonModule],
  templateUrl: './skills.component.html',
  styleUrls: ['./skills.component.css']
})
export class SkillsComponent {
  skillCategories: SkillCategory[] = [
    {
      name: 'Backend',
      icon: 'code',
      skills: ['C#', '.NET Core', 'Python', 'Node.js', 'Java']
    },
    {
      name: 'Frontend',
      icon: 'desktop',
      skills: ['Angular', 'React', 'TypeScript', 'HTML/CSS', 'Tailwind']
    },
    {
      name: 'Database',
      icon: 'database',
      skills: ['SQL Server', 'MongoDB', 'PostgreSQL', 'Redis']
    },
    {
      name: 'Cloud & DevOps',
      icon: 'cloud',
      skills: ['Azure', 'AWS', 'Docker', 'CI/CD']
    }
  ];
}
<section class="container mx-auto px-4 py-16">
  <h2 class="text-3xl font-bold mb-8 text-center">Technical Skills</h2>
  <div class="grid md:grid-cols-2 lg:grid-cols-4 gap-6 max-w-7xl mx-auto">
    <div *ngFor="let category of skillCategories" 
         class="bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition-all duration-300">
      <div class="flex items-center gap-2 mb-4">
        <i class="fas fa-{{category.icon}} text-blue-600"></i>
        <h3 class="text-xl font-bold text-blue-600">{{category.name}}</h3>
      </div>
      <div class="flex flex-wrap gap-2">
        <span *ngFor="let skill of category.skills" 
              class="px-3 py-1 bg-gray-100 rounded-full text-gray-700 text-sm hover:bg-blue-100 hover:text-blue-700 transition-colors duration-300 cursor-default">
          {{skill}}
        </span>
      </div>
    </div>
  </div>
</section>
