import { Component } from '@angular/core';
import { RouterLink, RouterLinkActive } from '@angular/router';
import { NgClass } from '@angular/common';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css'],
  standalone: true,
  imports: [RouterLink, RouterLinkActive], // Include the directives here
})
export class HeaderComponent {
  isNavVisible = false;

  toggleNav() {
    this.isNavVisible = !this.isNavVisible;
  }
}
<header class="header">
  <nav class="nav">
    <div class="logo-container">
      <img src="assets/profile.jpeg" alt="Logo" class="logo">
      <h1 class="title">Akshay Kumar</h1>
    </div>
    <ul class="nav-links">
      <li><a routerLink="/" routerLinkActive="active" [routerLinkActiveOptions]="{exact: true}" class="nav-link">Home</a></li>
      <li><a routerLink="/experience" routerLinkActive="active" class="nav-link">Experience</a></li>
      <li><a routerLink="/skills" routerLinkActive="active" class="nav-link">Skills</a></li>
      <li><a routerLink="/contact" routerLinkActive="active" class="nav-link">Contact</a></li>
      <li><a routerLink="/testimonials" routerLinkActive="active" class="nav-link">Testimonials</a></li>
    </ul>
    <button class="menu-button md:hidden">
      <svg class="menu-icon" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg">
        <path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M4 6h16M4 12h16m-7 6h7"></path>
      </svg>
    </button>
  </nav>
</header>
