import { Component } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FontAwesomeModule } from '@fortawesome/angular-fontawesome';
import { 
  faCode, 
  faDesktop, 
  faDatabase, 
  faCloud,
  faGears,
  faCubes,
  faNetworkWired,
  faShieldHalved,
  faBrain
} from '@fortawesome/free-solid-svg-icons';
import {
  faJava,
  faPython,
  faNodeJs,
  faAngular,
  faReact,
  faHtml5,
  faCss3,
  faAws,
  faDocker
} from '@fortawesome/free-brands-svg-icons';

interface SkillCategory {
  name: string;
  skills: Array<{
    name: string;
    icon: any;
    color: string;
  }>;
  icon: any;
  color: string;
}

@Component({
  selector: 'app-skills',
  standalone: true,
  imports: [CommonModule, FontAwesomeModule],
  templateUrl: './skills.component.html',
  styleUrls: ['./skills.component.css']
})
export class SkillsComponent {
  // Add new category icons
  faGears = faGears;
  faCubes = faCubes;
  faNetworkWired = faNetworkWired;
  faShieldHalved = faShieldHalved;
  faBrain = faBrain;
  // Category icons
  faCode = faCode;
  faDesktop = faDesktop;
  faDatabase = faDatabase;
  faCloud = faCloud;

  skillCategories: SkillCategory[] = [
    {
      name: 'Core Backend',
      icon: faCode,
      color: 'text-indigo-600',
      skills: [
        { name: 'C#', icon: faCode, color: 'text-purple-600' },
        { name: '.NET Core', icon: faCode, color: 'text-purple-700' },
        { name: 'Python', icon: faPython, color: 'text-yellow-600' },
        { name: 'Node.js', icon: faNodeJs, color: 'text-green-600' },
        { name: 'Java', icon: faJava, color: 'text-red-600' }
      ]
    },
    {
      name: 'Microservices',
      icon: faCubes,
      color: 'text-rose-600',
      skills: [
        { name: 'Service Discovery', icon: faNetworkWired, color: 'text-blue-500' },
        { name: 'API Gateway', icon: faNetworkWired, color: 'text-green-600' },
        { name: 'Event-Driven', icon: faCubes, color: 'text-purple-600' },
        { name: 'Message Queues', icon: faCubes, color: 'text-orange-500' },
        { name: 'Circuit Breakers', icon: faShieldHalved, color: 'text-red-500' }
      ]
    },
    {
      name: 'Architecture Patterns',
      icon: faGears,
      color: 'text-amber-600',
      skills: [
        { name: 'DDD', icon: faBrain, color: 'text-purple-600' },
        { name: 'CQRS', icon: faGears, color: 'text-blue-600' },
        { name: 'Event Sourcing', icon: faGears, color: 'text-green-600' },
        { name: 'Clean Architecture', icon: faCode, color: 'text-indigo-600' },
        { name: 'Hexagonal', icon: faCode, color: 'text-orange-600' }
      ]
    },
    {
      name: 'Scalability',
      icon: faNetworkWired,
      color: 'text-emerald-600',
      skills: [
        { name: 'Load Balancing', icon: faNetworkWired, color: 'text-blue-500' },
        { name: 'Caching', icon: faDatabase, color: 'text-red-500' },
        { name: 'Sharding', icon: faDatabase, color: 'text-green-500' },
        { name: 'Replication', icon: faDatabase, color: 'text-purple-500' },
        { name: 'High Availability', icon: faShieldHalved, color: 'text-orange-500' }
      ]
    },
    {
      name: 'Frontend',
      icon: faDesktop,
      color: 'text-blue-600',
      skills: [
        { name: 'Angular', icon: faAngular, color: 'text-red-600' },
        { name: 'React', icon: faReact, color: 'text-blue-400' },
        { name: 'TypeScript', icon: faCode, color: 'text-blue-600' },
        { name: 'HTML', icon: faHtml5, color: 'text-orange-600' },
        { name: 'CSS', icon: faCss3, color: 'text-blue-500' }
      ]
    },
    {
      name: 'Database',
      icon: faDatabase,
      color: 'text-green-600',
      skills: [
        { name: 'SQL Server', icon: faDatabase, color: 'text-red-500' },
        { name: 'MongoDB', icon: faDatabase, color: 'text-green-500' },
        { name: 'PostgreSQL', icon: faDatabase, color: 'text-blue-400' },
        { name: 'Redis', icon: faDatabase, color: 'text-red-600' }
      ]
    },
    {
      name: 'Cloud & DevOps',
      icon: faCloud,
      color: 'text-cyan-600',
      skills: [
        { name: 'Azure', icon: faCloud, color: 'text-blue-500' },
        { name: 'AWS', icon: faAws, color: 'text-orange-500' },
        { name: 'Docker', icon: faDocker, color: 'text-blue-600' },
        { name: 'CI/CD', icon: faCode, color: 'text-green-600' }
      ]
    },
    {
      name: 'Security & Testing',
      icon: faShieldHalved,
      color: 'text-violet-600',
      skills: [
        { name: 'OAuth/OIDC', icon: faShieldHalved, color: 'text-green-600' },
        { name: 'Unit Testing', icon: faCode, color: 'text-blue-500' },
        { name: 'Integration Tests', icon: faGears, color: 'text-purple-500' },
        { name: 'E2E Testing', icon: faNetworkWired, color: 'text-orange-500' },
        { name: 'Security Patterns', icon: faShieldHalved, color: 'text-red-500' }
      ]
    }
  ];
}
<section class="container mx-auto px-4 py-16">
  <h2 class="text-3xl font-bold mb-8 text-center">Technical Skills</h2>
  <div class="grid md:grid-cols-2 lg:grid-cols-4 gap-6 max-w-7xl mx-auto">
    <div *ngFor="let category of skillCategories" 
         class="bg-white p-6 rounded-lg shadow-lg hover:shadow-xl transition-all duration-300">
      <div class="flex items-center gap-2 mb-4">
        <fa-icon [icon]="category.icon" [class]="category.color + ' text-xl'"></fa-icon>
        <h3 class="text-xl font-bold" [class]="category.color">{{category.name}}</h3>
      </div>
      <div class="flex flex-wrap gap-2">
        <span *ngFor="let skill of category.skills" 
              class="flex items-center gap-1 px-3 py-1 bg-gray-100 rounded-full text-gray-700 text-sm hover:bg-blue-50 transition-colors duration-300 cursor-default">
          <fa-icon [icon]="skill.icon" [class]="skill.color"></fa-icon>
          {{skill.name}}
        </span>
      </div>
    </div>
  </div>
</section>
